void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

 vec2 xy = fragCoord.xy; //We obtain our coordinates for the current pixel
    xy.x = xy.x / iResolution.x; //We divide the coordinates by the screen size
    xy.y = xy.y / iResolution.y;
    // Now x is 0 for the leftmost pixel, and 1 for the rightmost pixel
    vec4 solidRed = texture(iChannel0,xy); 
   //This is actually black right now
     solidRed.r = xy.y;//Set its red component to the normalized x value
     solidRed.b = xy.y;
    if(xy.y > 0.8){
        solidRed.b = 1.0; //Set its red component to 1.0

        solidRed.r = 0.0; //Set its red component to 1.0
    }


    fragColor = solidRed;
}
