void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float cells = 4.0;
    vec2 grid0 = fract(uv * cells);
    vec2 grid1 = fract(vec2(1.0 - uv.x, uv.y) * cells);

    float everyOther = mod( floor(uv.x * cells) + floor(uv.y * cells), 2.0);


    vec2 coord = grid0 * everyOther + grid1 * (1.0-everyOther);

    fragColor = texture(iChannel0, coord);
}
