void mainImage( out vec4 O, vec2 U )
{
    O = T(U);
}

void mainImage( out vec4 O, vec2 u )
{
    O = T(u-vec2(1,0));
    vec2 U = u/iResolution.xy;

    float x = iTime;
    for(float i = 0.; i < 1e5*(.5+.5*sin(iTime)); i++)
        x = fract(1e5*sin(1e5*x));
    O.b = .1*x;

    if ( u.x==.5) O.r = float(U.y/4. < iTimeDelta ),
              //  O.g = float(U.y/4. < iTime-O.a); // equals iTimeDelta
                  O.g = float(U.y/4. < 1./iFrameRate);
    O.a = iTime;
}
