//change these constants to get different patterns!
#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
    //return abs(fract((a+c1.xy)*scale+iTime/500.)-.5); //morphing
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8./2.;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);   
    for(int k = 0; k < 9; k++){
        uv -= (t2.yx)*scale;
        vec2 t4 = vec2(0.);
        t3 = triangle_wave(-uv,scale)-t4;
        vec2 temp = t2;
        t2 = triangle_wave(-uv.yx-.5,scale)-t4;
        uv.yx = (t3-t2);
        t2 /= (temp-.75);
        //t2 /= abs(t2 - 2.5);
        col.x = 1.-abs(uv.x+uv.y+col.x);
        col = col.yzx;
        uv /= scale;
    }
    fragColor = vec4(1.-col,1.0);   
}
