void mainImage( out vec4 O, vec2 u )
{
    O = T(u);
}

// === game mechanism in 2D view ==========
// O.x = previous stored slice
// O.y = current moving slice
// O.w = try counter

void mainImage( out vec4 O, vec2 u )
{
    O = T(u);                                                 // previous shape
                                                              // move current slice
    O.y = T(u - R.x/6.*sin(2.*iTime)*(mod(O.w,2.)<1.?vec2(1,0):vec2(0,1)) ).x;

    if (iMouse.w > 0.)                                        // --- on click
        O.w++,                                                // try++
        O.x *= O.y;                                           // slice intersection

    vec2 U = ( 2.*u - R ) / R.y, A = abs(U);                  // --- init
    if (iFrame<1) O.x = step( max(A.x,A.y), .25)              // draw square
                     // step( length(U), .25)                 // disk instead of square
                     // * 2.*texture(iChannel1,U).x           // with texture
                        ;
}

// === 3D view + counter + stack ============================
// as in buffA + O.z = stack.

void mainImage( out vec4 O, vec2 u )
{
    vec2 U = ( 2.*u - R ) / R.y, H = 32.*u/R;  U.y -= .3;
 //                            inverse(mat2(2,-2,1,1))
    O = texture( iChannel1, U * mat2(1,2,-1,2)/4.*R.y/R +.5 ); // 3D view
    if (u.x < 10. && H.y < O.w && fract(H.y) > .1 ) O++;       // try counter

    O.z =   iFrame < 1  ? O.x                                  // init
          : iMouse.w > 0.                                      // onclick
              ? T(u+vec2(0,R.y/32.)).z + O.x                   //   stack
              : T(u).z;
}
