void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Parameters
    vec3 circleColor = vec3(0.85, 0.35, 0.2);
    float thickness = 0.1;
    // Control with mouse
    // thickness = iMouse.x / iResolution.x;
    float fade = 0.0;

    // -1 -> 1 local space, adjusted for aspect ratio
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    float aa = length(fwidth(uv));
    fade+=aa;
    // Calculate distance and fill circle with white
    float distance = 1.0 - length(uv);
    vec3 color = vec3(smoothstep(0.0, fade, distance));
    color *= vec3(smoothstep(thickness + fade, thickness, distance));
    color*=circleColor;
    color+=vec3(smoothstep(thickness,thickness+fade,distance));

    if(all(lessThan(uv,vec2(0.5,0.5))) && all(greaterThan(uv,vec2(-0.5, -0.5))))
    {
       color*= vec3(1.0, 0.0, 0.0);
    }

    fragColor = vec4(color, 1.0);

}
