// code by jianghn
vec3 catmul_rom(vec3 p0, vec3 p1, vec3 p2, vec3 p3, float t)
{
    return 0.5 * (
        (2.0 * p1) +
        (-p0 + p2) * t +
        (2.0 * p0 - 5.0 * p1 + 4.0 * p2 - p3) * t * t +
        (-p0 + 3.0 * p1 - 3.0 * p2 + p3) * t * t * t);
}

vec3 color_spline(float t)
{
    const int s = 12;

    vec3 p[s];
    p[0] = vec3(255, 0, 0) / 255.0;
    p[1] = vec3(0, 255, 0) / 255.0;
    p[2] = vec3(0, 0, 255) / 255.0;

    p[3] = vec3(255, 0, 0) / 255.0;
    p[4] = vec3(0, 255, 0) / 255.0;
    p[5] = vec3(0, 0, 255) / 255.0;

    p[6] = vec3(255, 0, 0) / 255.0;
    p[7] = vec3(0, 255, 0) / 255.0;
    p[8] = vec3(0, 0, 255) / 255.0;

    p[9] = vec3(255, 0, 0) / 255.0;
    p[10] = vec3(0, 255, 0) / 255.0;
    p[11] = vec3(0, 0, 255) / 255.0;

    float d = float(s-1) * t;

    int b = int(d);
    float dt = d - floor(d);

    return catmul_rom(p[((b-1)+s)%s], p[b], p[b+1], p[(b+2)%s], dt);
}

void mainImage(out vec4 result, in vec2 fragCoord)
{
    float t = fragCoord.y / iResolution.y;

    vec3 color = color_spline(t);

    result = vec4(color, 1.0);
}
