#define num 40.

float rand(vec2 v, float n){
    return fract(.05*n+55.7*sin(dot(3435.9*fract((v+3.1415)*5545.7),v)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime*.5;

    vec2 v = uv*num;
    vec2 s = abs(fract(v+(0.))-.5)*2.;
    float n = t;

    vec2 vf = mix(vec2(rand(floor(v+t),n)),vec2(rand(floor(v+t+.5),n)), s);   
    float cf = dot(vf,vec2(1.));
    fragColor = vec4(cos(t+vec3(1.5,2.2,2.3)*cf)*.5+.5,1.0);
    fragColor.xy *= .5;
}
