void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec2 uvDist = vec2(uv.xy);
	uvDist.x = uvDist.x + iChannelResolution[1].x * sin(iTime/100000.0);
	uvDist.y = uvDist.y + iChannelResolution[1].y * sin(iTime/100000.0);

	vec4 distortionColor = texture(iChannel1, uvDist);

	//uv.x = uv.x + iChannelResolution[0].x * sin(iTime/10000.0);
	//uv.y = uv.y + iChannelResolution[0].y * cos(iTime/10000.0);

	uv.x = uv.x + distortionColor.x / 200.0;
	uv.y = uv.y + distortionColor.y / 200.0;

	vec4 col = texture(iChannel0, uv);

	fragColor = col;
	//fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
}
