//Psuedo 3d First Person Camera movement.
//Pause & restart to hear the music.

mat2 rot2d(float angle){
    return mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = (fragCoord.xy-0.5*iResolution.xy) / iResolution.y;
    uv+= vec2(0.0, cos(iTime*4.0) / 12.0);

    vec2 uvp = vec2(uv.x / uv.y, 1.0 / abs(uv.y));

    float n = texture(iChannel1, uvp * 0.02+vec2(0.0,iTime*0.2)).r;

    uvp *= rot2d(uv.y);
    uvp += vec2(0.0, iTime*4.0); //move

    float mask = (abs(uv.y)-0.07) * 4.0;
    fragColor = texture(iChannel0, uvp*0.5) *(1.0+sin(iTime/mod(cos(iTime*3.),64.))/4.0);
    fragColor = mix(fragColor, vec4(0.8,0.3,0.0,1.0), n*(abs(uv.y)-0.13) * 4.0) * mask;

}
