void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float k = iResolution.x/iResolution.y;
    vec2 uv = (fragCoord / iResolution.xy - 0.5) * 10.0;
    uv.x *= k;
    float a = abs(sin(length(uv.x/cos(iTime))/length(uv.y))*sin(length(uv.y/cos(iTime))/length(uv.x)));
    fragColor = vec4(a,a,a,1.0);
}
