float rand(vec2 v){
    return fract(252.*sin(dot(3435.9*fract((v+3.1415)*555.7),v+2.1)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime*0.005;
    float f = rand(floor((t+uv)*120.));   
    vec3 c = cos(fract(f*(vec3(.1,.4,3.3)+t*88.))+1.);

    fragColor = vec4(c,1.0);
}
