void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    fragColor.rgb /= (fragColor.w + 1.0) / 20.0;
}

struct Shape {
    float dist;
    vec3 refl;
    vec3 emit;
};

// Hash from "Hash without Sine" by Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
float Hash31(in vec3 p) {
	p = fract(p * 0.1031);
    p += dot(p, p.yzx + 33.33);
    return fract((p.x + p.y) * p.z);
}

Shape add(in Shape a, in Shape b) {
    if (a.dist < b.dist) return a;
    return b;
}

Shape sub(in Shape a, in Shape b) {
    if (a.dist > -b.dist) return a;
    return Shape(-b.dist, b.refl, b.emit);
}

Shape inter(in Shape a, in Shape b) {
    if (a.dist > b.dist) return a;
    return b;
}

Shape map(in vec2 p) {
    return add(Shape(length(p - vec2(-0.25)) - 0.25, vec3(0.0), vec3(0.0)),
           add(Shape(length(p - vec2(0.25)) - 0.02, vec3(0.0), vec3(1.0)),
           Shape(max(abs(p.x - 0.4) - 0.2, abs(p.y - 0.15) - 0.05), vec3(0.0), vec3(0.0))));
}

vec2 getNormal(in vec2 p) {
    vec2 e = vec2(0.001, 0.0);
    return normalize(vec2(map(p + e.xy).dist - map(p - e.xy).dist,
                          map(p + e.yx).dist - map(p - e.yx).dist));
}

vec3 trace(in vec2 ro, in vec2 rd) {
    float t = 0.0;
    for (int i=0; i < 100; i++) {
        vec2 p = ro + rd * t;
        Shape scene = map(p);
        if (scene.dist < 0.001) {
            return scene.emit;
        }

        if (t > 10.0) {
            break;
        }

        t += scene.dist;
    }

    return vec3(0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec2 rd = sin(Hash31(vec3(uv * 100.0, iFrame)) * 6.28 + vec2(1.57, 0.0));
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);
    fragColor += vec4(trace(uv, rd) + 0.01 * step(0.0, -map(uv).dist), 1.0);
    if (iMouse.z > 0.0) fragColor = vec4(0.0);
}
