float normalizedSin(in float x)
{
    return (1.0 - cos(x)) / 2.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = texelFetch(iChannel0, ivec2(fragCoord), 0).x;
    fragColor = vec4(normalizedSin(x*2.), normalizedSin(x*4.), normalizedSin(x*3.), 1.0);
}

#define MOUSEADD 5000.
#define MOUSEMULTIPLY 1.1

float px( in ivec2 p )
{
    ivec2 r = ivec2(textureSize(iChannel0, 0));
    p = (p + r) % r;
    return texelFetch(iChannel0, p, 0).x;
}

float boxBlur(in ivec2 p)
{  
    float sum = px(ivec2(p.x, p.y-2)) + px(ivec2(p.x, p.y-1)) + px(ivec2(p.x-2, p.y)) + px(ivec2(p.x-1, p.y))
             + px(ivec2(p.x+1, p.y)) + px(ivec2(p.x+2, p.y)) + px(ivec2(p.x, p.y+1)) + px(ivec2(p.x, p.y+2));
    return sum / 8.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    ivec2 coord = ivec2(fragCoord);
    float o = boxBlur(coord);
    //o *= (sin(iTime*0.5) *0.003) + 1.0;
    if(length(fragCoord.xy-iMouse.xy) < 1.5 && iMouse.z > .1)
    {
        o += MOUSEADD;
        o *= MOUSEMULTIPLY;
    }
    fragColor = vec4(o, 0.0, 0.0, 0.0);
}
