float Hash21(vec2 p) {
    p = fract(p*vec2(.24687,.13579));
    p += dot(p, p+9.1);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 col = vec3(0);

    vec2 muv =(iMouse.xy-.5*iResolution.xy)/iResolution.y;
    muv = 7.*uv-muv*iTime;

    vec2 gv = fract(muv)-.5;
    vec2 id = floor(muv);

    float n = Hash21(id);
    float width = .19;

    if(n<.5) gv.x *= -1.;
    float d = abs(abs(gv.x + gv.y)-.5);

    vec2 cuv = gv-sign(gv.x + gv.y + .001)*.5;

    d = length(cuv);
    float mask = smoothstep(.01, -.01, abs(d-.5)-width);
    float angle = atan(cuv.x, cuv.y); // -pi to pi
    float chk = mod(id.x+id.y, 2.)*2.-1.;
    float flow = sin(iTime+chk*angle*12.);

    float x = fract(chk*angle/1.570796+iTime*.3);
    float y = (d-(.5-width))/(2.*width);
    //y = abs(y-.5)*2.;
    if(n<.5 ^^ chk>0.) y=1.-y;

    vec2 tuv = vec2(x,y);

    col += (1.-mask)*vec3(.5-uv.y, .5-uv.y*.55, .35);
    //col += (1.-mask)*vec3(uv+.5,.5); // original garish color scheme
    col += texture(iChannel0, tuv).rgb*mask;

    //if (gv.x>.48 || gv.y>.48) col = vec3(1,0,0);
    fragColor = vec4(col,1.0);
}
