const int RAY_STEPS = 256;
const float FOVY = 3.141569 * 0.25;
const float TO_RADIANS = 3.1415 / 180.0;
const vec3 LIGHT_POS = vec3(0., 6., 1.);
const vec3 LIGHT_COLOR = vec3(1, 0.7, 1);

const float SHADOW_HARDNESS = 6.0;
const float FIVETAP_K = 2.0;
const float AO_DIST = 0.085;


#define ATTENUATION 0

struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;
};

float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

float plane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

float box(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}


#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 3.5))
#define FLOOR_ID 2

#define TALL_BOX_SDF box(rotateY(pos + vec3(1.7, 1, -3), 27.5 * TO_RADIANS), vec3(1, 1, 1))
#define TALL_BOX_ID 3

#define SMALL_BOX_SDF box(rotateY(pos + vec3(-1.5, 0.5, -1.75), -27.5 * TO_RADIANS), vec3(1, 2, 1))
#define SMALL_BOX_ID 4

vec3 shootRay(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 eye2Ref  = ref - eye;
    float n = length(eye2Ref);

    vec3 worldUp = vec3(0.0, 1.0, 0.0);
    vec3 right  = normalize(cross(eye2Ref, worldUp));

    vec3 U = normalize(cross(right, eye2Ref));
    vec3 V = U * n * tan(FOVY * 0.5);

    float aspect = iResolution.x / iResolution.y;
    vec3 H = right * n * tan(FOVY * 0.5) * aspect;

    vec3 p = ref + ndc.x * H + ndc.y * V;
    return normalize(p - eye);
}


void sceneMap3D(vec3 pos, out float t, out int obj)
{
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2 = TALL_BOX_SDF;

    if(t2 < t)
    {
        t = t2;
        obj = TALL_BOX_ID;
    }
    t2 = SMALL_BOX_SDF;
    if(t2 < t)
    {
        t = t2;
        obj = SMALL_BOX_ID;
    }
}

float sceneMap3D(vec3 pos)
{
    float t = FLOOR_SDF;
    t = min(t, TALL_BOX_SDF);
    t = min(t, SMALL_BOX_SDF);
    return t;
}

float shadowMap3D(vec3 pos)
{
    float t = FLOOR_SDF;
    t = min(t, TALL_BOX_SDF);
    t = min(t, SMALL_BOX_SDF);
    return t;
}

vec3 computeNormal(vec3 pos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx) - sceneMap3D(pos - epsilon.yxx),
                            sceneMap3D(pos + epsilon.xyx) - sceneMap3D(pos - epsilon.xyx),
                            sceneMap3D(pos + epsilon.xxy) - sceneMap3D(pos - epsilon.xxy)));
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thick)
{
    float cosine = dot(-lightVec, n);

    switch(hitObj){
        case TALL_BOX_ID:
        return vec3(0.5, 1., 0.5) * cosine;
        break;
        case SMALL_BOX_ID:
        return vec3(1., .5, .5) * cosine;
        break;
        case FLOOR_ID:
        return cosine * softShadow(-lightVec, p, 0.1, 10.0) * LIGHT_COLOR;
        break;
        case -1:
        return vec3(0., 0., 0.);
    }
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - shadowMap3D(p + n * i * AO_DIST));
    }
    return 1.0 - k * aoSum;
}

Intersection sdf3D(vec3 dir, vec3 eye)
{
    float t;
    int objectID;
    march(eye, dir, t, objectID);
    vec3 intersection = eye + t * dir;
    vec3 nor = computeNormal(intersection);
    vec3 lightDir = normalize(intersection - LIGHT_POS);
    float thickness = fiveTapAO(intersection, -nor, FIVETAP_K);
    vec3 surfaceColor = computeMaterial(objectID, intersection, nor, lightDir, normalize(eye - intersection), thickness);
    return Intersection(t, surfaceColor, intersection, objectID);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) * 2. - vec2(1.0, 1.0);
    vec3 eye = vec3(0, 4, -15);
    vec3 ref = vec3(0, 0, 0);
    vec3 rayDir = shootRay(eye, ref, uv);
    Intersection it = sdf3D(rayDir, eye);
    fragColor = vec4(it.color, 1.0);
}
