#define PI 3.14152

float rand(float p){
 	return fract(sin(p*213.23)*55132.2);   
}

float noise(float p){
	float fl = floor(p);
  	float fc = fract(p);
	return mix(rand(fl), rand(fl + 1.0), fc);
}

vec2 rot(vec2 uv,float a){
	return vec2(uv.x*cos(a)-uv.y*sin(a),uv.y*cos(a)+uv.x*sin(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;

    vec2 polar = vec2(length(uv),atan(uv.y,uv.x) );
    polar.x += noise(polar.y*5.)*sin(polar.y)*0.2;
    polar.x = mod(polar.x-iTime*3.,.5);


    float radius = 0.1;
    float ringrad = .02;

    float ring = abs(polar.x-radius)-ringrad;
    ring = smoothstep( radius,radius-ringrad-0.1,ring);

    vec3 col = vec3(.1,.6,1.)*ring;

    fragColor = vec4(col,1.0);
}
