#define steps 13.
#define thicness .3
#define amplitude .8
#define speed 3.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float frequency = iMouse.x/500.;  
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (((fragCoord/iResolution.y))-.5);

    uv = vec2(uv.x/1., uv.y);

    float sinedValue=(round((abs(sin(uv.x+iTime*speed)-(uv.y/(amplitude/2.)))*steps))/steps)/thicness;

    vec3 col = vec3(sinedValue);

    // Output to screen
    fragColor = vec4(col,1.0);
}
