void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0, uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}

 #define _amount 6.
 #define PI 3.14159265358979323846

float cardiod(vec2 st, float pct, float thickness, float rot){
    float pos = length(st) + 0.4 * cos(atan(st.y,st.x) + rot);

    return smoothstep(pct-thickness, pct, pos) - smoothstep(pct, pct+thickness, pos);
}


vec2 rotate2D (vec2 _st, float _angle) {
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    return _st;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    uv = rotate2D(uv, iTime);
    uv = abs(uv);

    vec4 color = vec4(0,0,0.4,0);

    for(float i=0.;i<_amount;i++){
        float card = cardiod(uv, cos(iTime*2.)*0.2, sin(iTime) * 0.03, (sin(iTime)) + i);

        color += card;
    }

    float buff = texture(iChannel0, (fragCoord)/iResolution.xy).r*.9;

    color += vec4(buff, buff*0.2, buff*0.5, 0.);

    // Output to screen
    fragColor = color;
}
