vec3 red = vec3(0.894,0.118,0.039);
vec3 lightblue = vec3(0.384,0.725,0.914);
vec3 blue = vec3(0.18,0.137,0.51);

float circle(vec2 p, float r, float e) {
    p = mod(p, 0.9) - .5;
    p = abs(p);                   // norm(e,v)
    p.y > p.x ? p = p.yx : p;
    p = e * log(p) ;
    e = exp( ( p + log(1. + exp(p.y - p) ) ) / e ).x;

	return smoothstep(1.5, 0., (e-r) / fwidth(e) );
}

void mainImage( out vec4 oFragColor, in vec2 iFragCoord ) {
    vec2 p = (2.*iFragCoord) / iResolution.y;
	float size = 0.25;
	float r = circle(p + 0.13 * iTime, size * (0.9 + 0.1 * cos(1.2 * iTime)), pow(64.0, 0.7 + 0.2 * sin(1.3 * iTime)));
    float b = circle(p + vec2(-0.1 * iTime, 0.5 + 0.1 * iTime), size * (0.9 + 0.1 * sin(1.1 * iTime)), pow(64.0, 0.6 + 0.2 * cos(1.7 * iTime)));

    vec3 c = 1.0 - (r * (1.0 - red) + b * (1.0 - lightblue));
    c = mix(c, blue, clamp((r + b) - 1.0, 0., 1.));
    oFragColor += vec4(c, 1.0);
}
