void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ratio = vec2(16.00, 9.0) * 10.0;
    uv *= ratio;
    vec2 uvLocal = fract(uv) * 2.0 - 1.0;
    uv = floor(uv);
    uv /= ratio;

    vec3 col = texture(iChannel0, uv).rgb;

    // Time varying pixel color
    vec3 col2 = texture(iChannel1, uv).rgb;
    float avg = (col2.r + col2.g + col2.b) / 3.0;
    avg = 0.5 + avg / 2.0;
    col2 = col2 * smoothstep(1.0, 1.25, avg / length(uvLocal));
    col = mix(col, col2, avg);

    float pctG = col.g / (col.r + col.g + col.b);

    vec3 col3 = texture(iChannel2, uv).rgb;
    col = mix(col, col3, smoothstep(0.25, 0.75, pctG));

    float br = (col.r + col.g +col.b) / 3.0;

    vec3 col4 = texture(iChannel3, uv).rgb;
    col = mix(col, col4, smoothstep(0.0, 1.0, br));


    // Output to screen
    fragColor = vec4(col, 1.0);
}
