void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 1.0f - abs(fragCoord/iResolution.xy - vec2(0.5f));
    float g = mod(iTime,25.0f);
    uv *= g;
    float a = mod(floor(uv.x + uv.y), g);
    vec3 col = vec3(0.05f, 1.0f, 0.05f) * 1.2 * a;
    col /= g;

    // Output to screen
    fragColor = vec4(col,1.0);
}
