void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 p1=vec2((cos(iTime*4.0/5.0)/2.0+0.5)*iResolution.x, (sin(iTime*3.0/5.0)/2.0+0.5)*iResolution.y);
    vec2 p2=vec2((cos(iTime*7.0/5.0)/2.0+0.5)*iResolution.x, (sin(iTime*5.0/5.0)/2.0+0.5)*iResolution.y);
    vec2 p3=vec2((cos(iTime*3.0/5.0)/2.0+0.5)*iResolution.x, (sin(iTime*4.0/5.0)/2.0+0.5)*iResolution.y);

    float d1=distance(p1, fragCoord)/4000.;
    float d2=distance(p2, fragCoord)/4000.;
    float d3=distance(p3, fragCoord)/4000.;

    vec3 col = texture( iChannel0, vec2(sqrt(d1*d2*d3), 0.0)).xyz;

    fragColor = vec4(col*1.3,1.0);
}
