float fbm(vec2 p)
{
    p /= 50.;
    mat2 m = mat2(.8, -.6, .6, .8); 
    float amp = .5;

    float t=0.;
    float f=1.;
    for (int i = 0; i < 7; i++)
    {
        t += amp * texture(iChannel0, (p+vec2(iTime*.002, 0.))*f).r;
        p *= m * 8.*sin(iTime*.2);
        f *= 2.;
        amp *= .5;
    }

    return t;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.y;

    vec2 p = uv;//vec2(atan(uv.y,uv.x)*3.1415*1., dot(uv,uv)+iTime);
    //p.y -= iTime;
    float v = fbm(p+fbm(p+fbm(p+fbm(p))));

	fragColor = vec4(1., 1.-v, v*.3, 1.);
}
