void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
   vec2 pos = fragCoord.xy;
   pos.x /= iResolution.x;
   pos.y /= iResolution.y;
   vec4 solidColor = vec4(0, 0, 0, 1);
   if(pos.x > 0.5)
   {
      solidColor.g = 1.0;
   }

   if(pos.y > 0.5)
   {
      solidColor.r = 1.0;
   }

   fragColor = solidColor;
}
