void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float zoom = 0.0;

    if (iMouse.x > iResolution.x*0.95 && iMouse.z > 0.0){
        zoom = iMouse.y;
    }

    ivec2 res = ivec2(iResolution.xy/zoom);
    ivec2 coord = ivec2(fragCoord.xy/zoom);

    int m = res.x; //the middle of the array
    int l = 0; //left index
    int r = res.x-1; //right index
    int c = -1; //itterations
    for (int i = 0; i < res.x; i++) {
        m = (r+l)/2;
        if (m == coord.x) {
            c = i;
            break;
        } else if (m < coord.x) {
            l = m-1;
        } else if (m > coord.x) {
            r = m+1;
        }
    }
    if (fragCoord.x > iResolution.x*0.99){
        if (round(fragCoord.y)==round(iMouse.y/10.0)*10.0) {
            fragColor = vec4(1);
        } else {
            fragColor = vec4(0.1,0.8,0,1);
        }
    } else {
        fragColor = vec4(coord.y == c);
    }
}
