// Fork of "Circle Sandbox" by lmno. https://shadertoy.com/view/NdlSD7
// 2021-04-18 04:11:54


// Drawing utilities
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, -0.05, dist))

// SDFs
float sdDisc(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdInfLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * dot(pa, ba) / dot(ba, ba));
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;

    vec4 mouse = (vec4(iMouse.xy, abs(iMouse.zw)) - center.xyxy) / iResolution.y * 4.0;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    float unit = 9.0 / iResolution.y;
    vec3 color = vec3(0.075,0.094,0.604);

    // Default before interaction
    if (ivec2(iMouse.xy) == ivec2(0)) {
        mouse.xy = vec2(sin(iTime), 1.0) * cos(iTime);
        mouse.x += 2.0;
    }

    // Radius and angles
    float r = 0.75;
    vec2 a = atan(mouse.y, mouse.x) + vec2(2.0, -1.9) * acos(r / length(mouse.xy));

    // For just the points, this can be simplified (likely no sin/cos, just 1 or 2 sqrts)
    vec2 t1 = vec2(cos(a.x), sin(a.x)) * r;
    vec2 t2 = vec2(cos(a.y), sin(a.y)) * r;
    vec2 t3 = vec2(cos(a.x), sin(a.y)) * r;
    vec2 t4 = vec2(cos(a.y), sin(a.x)) * r;
    vec2 t5 = vec2(sin(a.y), sin(a.y)) * r;
    vec2 t6 = vec2(sin(a.x), sin(a.x)) * r;
    vec2 t7 = vec2(cos(a.y), cos(a.y)) * r;
    vec2 t8 = vec2(cos(a.x), cos(a.x)) * r;
    vec2 t9 = vec2(sin(a.x), cos(a.x)) * r;
    vec2 t10 = vec2(sin(a.y), cos(a.y)) * r;
    vec2 t11 = vec2(tan(a.y), tan(a.y)) * r;
    vec2 t12 = vec2(tan(a.x), tan(a.x)) * r;
    vec2 t13 = vec2(tan(a.x), sin(a.x)) * r;
    vec2 t14 = vec2(tan(a.y), sin(a.y)) * r;
    vec2 t15 = vec2(sin(a.y), tan(a.y)) * r;
    vec2 t16 = vec2(sin(a.x), tan(a.x)) * r;
    vec2 t17 = vec2(log(a.x), log(a.x)) * r;
    vec2 t18 = vec2(log(a.y), log(a.y)) * r;
    vec2 t19 = vec2(log(a.y), sin(a.y)) * r;


    // Circle
    drawSDF(abs(sdDisc(uv, vec2(0.0), r)), vec3(0.000,0.0,1.000));

    // Right triangle 1
    drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t1),
                sdLine(uv, t1, vec2(0.0)))), vec3(0.000,1.0,1.000));

    // Right triangle 2
    drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t2),
                sdLine(uv, t2, vec2(0.0)))), vec3(0.000,1.000,1.000));


                 drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t3),
                sdLine(uv, t3, vec2(0.0)))), vec3(0.0, 1.0, 1.0));


                                 drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t4),
                sdLine(uv, t4, vec2(0.0)))), vec3(0.0, 1.0, 1.0));




                                        drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t5),
                sdLine(uv, t5, vec2(1.0)))), vec3(0.0, 1.0, 1.0));





                                        drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t6),
                sdLine(uv, t6, vec2(1.0)))), vec3(0.0, 1.0, 1.0));



                                        drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t7),
                sdLine(uv, t7, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                        drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t8),
                sdLine(uv, t8, vec2(1.0)))), vec3(0.0, 1.0, 1.0));



                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t9),
                sdLine(uv, t9, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t10),
                sdLine(uv, t10, vec2(1.0)))), vec3(0.0, 1.0, 1.0));





                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t11),
                sdLine(uv, t11, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t12),
                sdLine(uv, t12, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t13),
                sdLine(uv, t13, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t14),
                sdLine(uv, t14, vec2(1.0)))), vec3(0.0, 1.0, 1.0));



                                                drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t15),
                sdLine(uv, t15, vec2(1.0)))), vec3(0.0, 1.0, 1.0));



                                                      drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t16),
                sdLine(uv, t16, vec2(1.0)))), vec3(0.0, 1.0, 1.0));




                                                      drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t17),
                sdLine(uv, t17, vec2(1.0)))), vec3(0.0, 1.0, 1.0));


                                                      drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t18),
                sdLine(uv, t18, vec2(1.0)))), vec3(0.0, 1.0, 1.0));           



                                                   drawSDF(min(sdLine(uv, vec2(0.0), mouse.xy),
            min(sdLine(uv, mouse.xy, t19),
                sdLine(uv, t19, vec2(1.0)))), vec3(0.0, 1.0, 1.0));       




    // Tangents
    drawSDF(sdInfLine(uv, mouse.xy, t1), vec3(0.0, 0.0, 1.0));
    drawSDF(sdInfLine(uv, mouse.xy, t2), vec3(0.0, 0.0, 1.0));
    drawSDF(sdInfLine(uv, mouse.xy, t3), vec3(0.0, 0.0, 1.0));
    drawSDF(sdInfLine(uv, mouse.xy, t4), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t5), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t6), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t7), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t8), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t9), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t10), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t11), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t12), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t13), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t14), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t15), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t16), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t17), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t18), vec3(0.000,0.0,1.000));
    drawSDF(sdInfLine(uv, mouse.xy, t19), vec3(0.000,0.0,1.000));


    // Point
    drawSDF(sdDisc(uv, mouse.xy, 0.005), vec3(2.0));

    fragColor = vec4(color, 2.0);
}
