float f(float x)
{ return 16. * x * x * (1.-x) *(1.-x);}

float tri(float x)
{
return 3. * (2. * max(fract(x),fract(-x))-1.)+2.;
}

float func(vec2 uv, float t)
{    
float b = f(0.5 * (1. + cos(t)));
vec2 dir = uv - 0.5;
float theta = atan(dir.y,dir.x);

    float k = round(tri( t /(6.* 3.14159)));  
float d = (1.-b) * length(dir) * (1. + 0.1* cos(k * theta + 3. * t))
+ b * b * (pow(abs(dir.x),b)+pow(abs(dir.y),b));
d = f(3.* d);

return step(d +0.01,sqrt((1.-b)*(1.-b) + b* b));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y - vec2(0.375,0);
   // uv = 0.5 * uv + 0.5 * floor(99. * uv)/99. ;

      float t =iTime;

vec3 col = vec3(func(uv,t +0.024), func(uv,t), func(uv, t -0.024));

    fragColor = vec4(col,1.0);
}
