void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse=iMouse.xy/iResolution.xy;

    float scale=0.8;
	float xoffset=(mouse.x-0.5)*0.25;
	float yoffset=(mouse.y-0.5)*0.1;


    float posx=0.5;
    float posy=0.5;

    uv=vec2(uv.x-posx,uv.y-posy);

	float xo=clamp(xoffset,-0.2*posx, 0.2*(1.0-posx));
	float yo=clamp(yoffset,-0.2*posy,  0.2*(1.0-posy));
    uv.x+=xo;
    uv.y+=yo;

	vec3 col=texture(iChannel0,clamp(vec2(uv.x*scale+posx,uv.y*scale +posy),0.0,1.0)).rgb; 


    fragColor=vec4(col,1.0);
}
