const float AA = 0.1;

// @param source 0..1
// @return 0..1
float lines(float source) {
    float sx = mod(source, 0.1) * 10.0;
    float tint1 = smoothstep(0.5 - AA, 0.5, sx);
    float tint2 = smoothstep(0.5 + AA, 0.5, sx);
    float tint = tint1 * tint2;
    return tint;
}

// @param value 0..1
// @return -0.2..0.8
float remap(float value) {
    float minimum = -0.2;
    return minimum + (1.0 - minimum) * value;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {    
    vec2 uv = fragCoord / iResolution.xx;
    float lines1 = lines(uv.x - uv.y);
    float lines2 = lines(uv.x + uv.y);
    float tint = (lines1 + lines2);
    float red1 = remap(tint);
    float sx = uv.x - sin(iTime * 3.0);
    float green1 = remap(tint * (1.0 - sx));
    float blue1 = remap(tint * (1.0 - sx));
    float red2 = remap(tint * sx);
    float green2 = remap(tint);
    float blue2 = remap(tint * sx);
    float red = red1 * red2;
    float green = green1 * green2;
    float blue = blue1 * blue2;
    fragColor = vec4(vec3(red, green, blue), 1.0);
}
