#define RotMat(val) mat2(cos(val), -sin(val), sin(val), cos(val))

highp float theMoon(vec2 uv, float sharpness){
	float DarkSideSize = 6.8;
	float MoonSize = 6.9;
	float NewMoon = 0.0;
	float CrescentMoon = 0.1;
	float FirstQuarter = 0.5;
	float FullMoon = 2.0;


/// Moon phase setting
	float m1 = NewMoon,
				m2 = CrescentMoon,
				m3 = FirstQuarter,
				m4 = FullMoon;

/*
		Write the phase you want.
	example: "m2" for Crescent moon.
*/
	float Phase = m2;


	//Rotate the moon the way you want
	uv *= RotMat(0.9);

float moonShape =
	(sqrt(clamp(1.0-length((uv*MoonSize)),0.0,1.0)*sharpness)
		-
	clamp(1.0-length((uv*DarkSideSize)-vec2(Phase,0.0)), 0.0, 1.0)*sharpness);

	float moonRes = clamp(moonShape, 0.0, 1.0);
	return moonRes;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x-vec2(0.5,0.4);


    // Output to screen
    fragColor = vec4(vec3 (theMoon(uv, 10.0)),1.0);
}
