vec3 goldGradient(float t) {
	vec3 c = vec3(0.9923842, 1.0102498, 0.106);
	c.r += -0.7307684 / (1.0 + exp(7.55021*t));
	c.g += -1.3544996 / (1.0 + exp(2.61615*t));
	c.b += (0.054*t + 0.295) * t;
	return clamp(c, 0.0, 1.0);
}

void mainImage(out vec4 oFragColor, in vec2 iFragCoord) {
    oFragColor.rgb = goldGradient(iFragCoord.x / iResolution.x);
}
