#define R iResolution.xy
#define T iTime
#define dT iTimeDelta
#define I iFrame
#define N 250
#define Main void mainImage(out vec4 Q, in vec2 V)
#define ei(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define A(U) texture(iChannel0,(U)/R)
vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {
    Q = vec4(0);

    vec2 U = .7*(V-.5*R)/R.y;
    U += vec2(.05,.6);
    float M =  min(10.*T,50.);
    float l = .3;
    float s = 1.;
    vec2 v = vec2(0);
    for (float i = 1.; i < M; i++) {
        vec2 p = U.xy;
        vec2 a = vec2(0);
        vec2 b = vec2(0,l);
        float x = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
        float ln = length(p-a-(b-a)*x);

        float sml = 1./i*exp(-1e5*i*ln*ln);
        if (i>3.) Q += sml;
         U.y -= l;
         s = sign(U.x);
         if (hash(vec4(V.xy,i,I)).x<.1)s *= -1.;
         U.x = s*(U.x);
         U.xy *= ei(.6+5.*l);
         l *= .75-.3*l+.1*s*(mod(i,2.)*2.-1.);
    }
    Q = max(Q,A(V));
    if (I < 1) Q = vec4(0);
 }
