void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    float angle = iTime*0.008;

    vec2 normalizedCoord = fragCoord / iResolution.xy * 2.0 - 1.0;

    for (float i = 0.0; i < 64.0; i += 1.0) {
    normalizedCoord = abs(normalizedCoord);
    normalizedCoord -= 0.5;
    normalizedCoord *= 1.1;
    normalizedCoord *= mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
    };
    fragColor = vec4(vec3(length(normalizedCoord + vec2(sin(iTime)*.5, sin(-iTime)*.5)),length(normalizedCoord + vec2(sin(-iTime)*.5, cos(iTime)*.5)),length(normalizedCoord + vec2(cos(iTime)*.5, sin(-iTime)*.5))),0);
}
