#define deg 0.01745329251

float rangeConvert(float OldValue, float OldMin, float OldMax, float NewMin, float NewMax){
    return (((OldValue - OldMin) * (NewMax - NewMin)) / (OldMax - OldMin)) + NewMin;
}
float myRangeConvert(float v){
    return v * .8 + .2;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    uv = abs(uv);
    uv = vec2(max(uv.x, uv.y), min(uv.x, uv.y));

    vec3 col1 = vec3(1, 0, 0) * (1. - step(0.005, uv.y) ) *
                    clamp(sin((iTime * 30. - 30. * (1. - (.25 - uv.x) / .25) ) * deg), 0., 1.);
    vec3 col2 = vec3(1, 0, 0) * (1. - step(0.005, abs(uv.x - .25 - uv.y) ) ) *
                    clamp(sin((iTime * 30. - 30. - 30. * (1. - (.5 - uv.x) / .25) ) * deg), 0., 1.);
    vec3 col3 = vec3(1, 0, 0) * (1. - step(0.005, abs(.5 - uv.x) ) ) * step(.25, uv.y) *
                    clamp(sin((iTime * 30. - 60. - 30. * (1. - (.5 - uv.y) / .25) ) * deg), 0., 1.); 


    vec3 col = mix(col1, col2, step(.25, uv.x));
    float mixv = smoothstep(.495, .5, uv.x);
    col = col * (1. - mixv) + col3 * mixv;

    fragColor = vec4(col * (1. - step(.5, uv.y)), 1.);
}

