const float SMOOTHNESS = .005;

const vec2 BASE_POS = vec2(.0, .0);
const vec2 EYE_POS = vec2(.2, -.001);

const float MOUTH_SIZE = 0.07;

float Circle(vec2 uv, vec2 position, float radius, float blur){
    float dist = length(uv - position);
    float circle = smoothstep(radius, radius - blur, dist);

    return circle;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x / iResolution.y;
    vec3 col = vec3(0.);

    float mask = Circle(uv, BASE_POS, .4, SMOOTHNESS);

    mask -= Circle(uv, EYE_POS, .1, SMOOTHNESS);
    mask -= Circle(uv, -EYE_POS, .1, SMOOTHNESS);

    float mouth = Circle(uv, vec2(0., 0.), MOUTH_SIZE, SMOOTHNESS);
    mouth -= Circle(uv, vec2(0., .04), MOUTH_SIZE, SMOOTHNESS);

    mask -= mouth;

    col = vec3(1., 1., 0) * mask;

    fragColor = vec4(col, 1.0);
}
