void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    float s = sin((uv.x-iTime*.01)*25.)*cos((uv.y+iTime*.02)*25.)*.5+.5;
    uv += sin(s)*cos(s)+atan(s-uv.x); 
    uv = fract(uv+cos(iTime*.3+sin(uv.y))*sin(iTime*.2+cos(uv.x)));
    vec3 col = vec3(uv.x+sin(iTime)*.1+.1, uv.y+cos(iTime)*.1, uv.y+cos(iTime*2.3)*.1+.1);
    // Output to screen
    fragColor = vec4(col,1.0);
}
