#define S(a, b, t) smoothstep(a, b, t)

vec3 fromHex(vec3 c){
    return vec3(c.x/255., c.y/255., c.z/255.);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return S(-.01, .01, length(max(d,0.0)) + min(max(d.x,d.y),0.0));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    uv += .2;
    uv /= .5;
    // Time varying pixel color
    vec3 col = vec3(.0);
    float sin01 = .5*(1. + sin(iTime));
    float sin12 = 1. + sin01;

    float bot = sdBox(vec2(uv.x-.64*sin12-uv.y*1.65, uv.y+.15*sin12+uv.x*.21), vec2(.537, .245));
    col = mix(vec3(.5, .7, .1), col, bot);

    float back = sdBox(vec2(uv.x-.59-sin01, uv.y-.48-sin01+uv.x*.2), vec2(.398, .392));
    col = mix(vec3(1., .8, .4), col, back);

    float rSide = sdBox(vec2(uv.x-.69-sin01, uv.y+.32+sin01-uv.x*.6), vec2(.31, .399));
    col = mix(fromHex(vec3(51., 51., 51.)), col, rSide);


    float lSide = sdBox(vec2(uv.x+.10+sin01, uv.y-.32-sin01-uv.x*.6), vec2(.3, .399));
    col = mix(vec3(.8, .5, .4), col, lSide);

    float front = sdBox(vec2(uv.x+sin01, uv.y+sin01+uv.x*.2), vec2(.4, .4));
    col = mix(fromHex(vec3(125., 125., 125.)), col, front);

     float top = sdBox(vec2(uv.x+.66*sin12-uv.y*1.65, uv.y-.638*sin12+uv.x*.21), vec2(.537, .245));
    col = mix(fromHex(vec3(142., 142., 142.)), col, top);

    // Output to screen
    fragColor = vec4(col,1.0);
}
