#define COUNT 64
#define FARCLIP 4.0
#define BIAS 0.2
#define Offset 18.9
#define radius 0.15

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
	vec2 uv = fragCoord.xy / iResolution.xy;    


    vec4 norz = texture(iChannel0, uv);

    float depth = norz.w * FARCLIP;
    float scale = radius / depth;

    float ao = 0.0;
    for(int i = 0; i < COUNT; i++)
    {
        vec2 randUv = (fragCoord.xy + Offset * float(i)) / iChannelResolution[1].xy;
        vec3 randNor = texture(iChannel1, randUv).xyz * 2.0 - 1.0;
        if(dot(norz.xyz, randNor) < 0.0)
            randNor *= -1.0;

        vec2 off = randNor.xy * scale;
        vec4 sampleNorz = texture(iChannel0, uv + off);
        float depthDelta = depth - sampleNorz.w * FARCLIP;

        vec3 sampleDir = vec3(randNor.xy * radius, depthDelta);        
        float occ = max(0.0, dot(normalize(norz.xyz), normalize(sampleDir)) - BIAS) / (length(sampleDir) + 1.0);
        ao += 1.0 - occ;
    }    
    ao /= float(COUNT);

    fragColor = vec4(vec3(ao), 1.0);
}

#define FARCLIP 10.0

float SdPlane( vec3 p )
{
	return p.y;
}

float SdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float SdSphere( vec3 p, float s )
{
    return length(p) - s;
}

float OpU( float d1, float d2 )
{
	return min(d1, d2);
}

float OpS( float d1, float d2 )
{
    return max(-d2, d1);
}

float Map( vec3 pos )
{
    float res = SdPlane(pos);    
    res = OpU( res, SdSphere(pos - vec3(0.7, 0.4, 0), 0.5));
    res = OpU( res, SdSphere(pos - vec3(-0.7, 0.5, 0), 0.75));
    res = OpU( res, SdBox(pos-vec3(0,0.5,1.5),vec3(0.5)));

    return res;
}

float CastRay( vec3 camPos, vec3 rayDir )
{
    float nearClip = 0.1;

    float dis = nearClip;
    for(int i = 0; i < 50; i++)
    {
	    float res = Map(camPos + rayDir * dis);
        if(res < 0.001) 
            break;

        dis += res;
        if(dis > FARCLIP)
        {
            dis = -1.0;
            break;
        }
    }

    return dis;
}

//---render-------------------------------------------------
vec3 GetNormal( vec3 pos )
{
    vec3 eps = vec3(0.001, 0.0, 0.0);
	vec3 nor = vec3(Map(pos + eps.xyy) - Map(pos - eps.xyy),
                    Map(pos + eps.yxy) - Map(pos - eps.yxy),
                    Map(pos + eps.yyx) - Map(pos - eps.yyx));
	return normalize(nor); 
}

vec4 Render( vec3 camPos, vec3 localViewDir, mat3 cam2worldMatrix )
{
    vec4 col;    
    vec3 pos = camPos;
    vec3 rayDir = cam2worldMatrix * localViewDir;

    float dis = CastRay(pos, rayDir);
    if(dis > 0.0)
    {
        pos += rayDir * dis;

        vec3 nor = GetNormal(pos);
        nor = inverse(cam2worldMatrix) * nor;
        nor.z *= -1.0;

        float zDelta = localViewDir.z;
        zDelta *= dis / FARCLIP;

        col = vec4(nor, zDelta);
    }
    else
    {
        col = texture(iChannel0, rayDir);
    }

    return col;
}
//--------------------------------------------------------

mat3 SetCamera( vec3 ro, vec3 ta, float cr )
{
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr), 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = q * 2.0 - 1.0;
	p.x *= iResolution.x / iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 camPos = vec3(3.0 * cos(7.0 * mouse.x), 1.0 + 2.0 * mouse.y, 3.0 * sin(7.0 * mouse.x));
    vec3 camLookAt = vec3(0.0, 1.0, 0.0);

    mat3 cam2worldMatrix = SetCamera(camPos, camLookAt, 0.0);

    vec3 localViewDir = normalize(vec3(p.xy, 1.5));

    fragColor = Render(camPos, localViewDir, cam2worldMatrix);
}
