{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 color = texture(iChannel0, uv);
    uv -= 0.5;
    uv.y = sin(iTime);
    vec4 oneVec = vec4(1.0, 1.0, 1.0, 1.0);
    vec4 zeroVec = vec4(0.0);

    vec4 fragColor2 = color;


    vec4 a = color*(1.0 - mod(length(uv),1.0));
