#define PI 3.141592653589793

vec2 resetAxies(in vec2 fragCoord) {
    fragCoord = 3.0 * (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
    return fragCoord;
}

vec3 renderHeart(in vec2 fragCoord) {

    vec3 color = vec3(0.0, 0.0, 0.0);
    float y = fragCoord.y;
    float x = fragCoord.x;

    if (x < 0.0) {
        x = -x;
    }

    float r = pow(x, 2.0 / 3.0) + pow(3.3- pow(x, 2.0), 0.5) * sin(iTime * PI* x);
    float d = abs(r - y);
    if (d < 1.0 * fwidth(fragCoord.y)) {
        color = 0.5 + 0.5*cos(iTimeDelta + fragCoord.xyx+vec3(0,2,4));
    }

    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = resetAxies(fragCoord);
    vec3 color = renderHeart(uv);
    fragColor = vec4(color, 1.0);
}
