void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

vec4 circle(float radius, vec2 position, vec2 uv, vec3 color){
    float glow = 0.09;
    float res = smoothstep(radius, radius/5.-glow, length(uv-position));
    return vec4(vec3(res)/color,5.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ( fragCoord -.5* iResolution.xy) / iResolution.y;

    vec4 color = vec4(0.);

    for (int i = 0; i < 2000; i++){
        vec4 config = texture(iChannel1, vec2(i)*0.001);
        float speed = texture(iChannel2, vec2(i)*0.001).r*2.;
        //vec2 position = 
        //if 
        color += circle(config.r*0.02, vec2(cos(iTime*speed)*config.g, sin(iTime*speed)*config.g), uv, vec3(config));
    }

    vec4 pfragColor = texture(iChannel0, fragCoord.xy / iResolution.xy)/1.05;

    fragColor = color+pfragColor;
}
