vec2 triangle_wave(vec2 a){
    return abs(fract((a+vec2(1.,0.5))*1.5)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 4.*8.*4.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    float time1 = iTime/64.;
    uv += vec2(time1/2.0,time1/3.0)/t1/4.0+(vec2(cos(time1),sin(time1)))*8./t1;
    float scale = 1.5;
    vec2 t2 = vec2(0.);
    vec2 t3 = vec2(0.);
    for(int k = 0; k < 12; k++){
        uv += t2;
        uv /= scale;
        uv.x += sign(uv.x)/2.;
        t2 = triangle_wave(uv-.5);
        t3 = -triangle_wave(uv.yx+1.);
        uv = t2-t3;
        col.x = max(uv.y-uv.x,col.x/2.);
        //col.x = abs(.5-col.x);
        col = abs(col.yzx-vec3(col.x)*2.);
    }
    fragColor = vec4(col*2.,1.0);
}
