// Set whether or not to loop the animation
// NOTE: If it's set to 0, you'll have to reset the time after DURATION seconds have passed
#define LOOP 1

// #define PIXELATE_DISTORT
// #define PIXELATE_DROPLETS
// #define PIXELATE_WARP

// Pixel size for pixelation
const float PIXEL_SIZE = 4.0;
// Droplet size relative to screen
const vec2 DROP_SIZE = vec2(0.0625, 0.125) * 2.0;
// Droplet life relative to DURATION
const vec2 DROP_LIFE = vec2(0.125, 1.0);
// Drop rim smoothness - the smaller the value, the sharper the drop rim
const float DROP_RIM = 1.0 / 512.0;
// Drop limit multiplier - the larger the value, the sharper the cutoff
// This is an extra multiplier applied to the drops so their effect doesn't extend over their own cell
const float DROP_LIMITER = 1.0;
// Drop distortion strength - the larger the value, the more turbulent the distort on the inside of the drops
const float DROP_DISTORT_STRENGTH = 0.125;
// Trail smoothness - the larger the value, the smoother the edges of drop trails
const float TRAIL_SMOOTH = 1.0 / 128.0;
// Trail darkness - the larger the value, the darker the inside of the trails
const float TRAIL_DARKEN = 0.05;
// Distortion frequency - smaller values make the drop paths and shapes more turbulent
const vec2 DISTORT_SIZE = vec2(0.125);
// Distortion strength relative to screen - larger values give drops weirder shapes
const float DISTORT_STRENGTH = 0.04;
// Blur samples - note that this is squared, i.e. 8 samples result in 64 texture samples
// Decrease this for better performance, at the cost of worse blurs
const int BLUR_SAMPLES = 8;
// Blur size relative to screen
const float BLUR_SIZE = 1.0 / 64.;
// Screen warp frequency - smaller values make for more turbulent screen warp
// NOTE: xy is relative to screen, z is relative to time
const vec3 WARP_FREQ = vec3(.25, .25, .5);
// Screen warp strength - larger values make for larger distortions in warping
const float WARP_STRENGTH = 0.0625f;

// Effect strength
// NOTE: This is set automatically depending on LOOP
// NOTE: To set manually, you can decrease the value from 1 to 0 as the effect fades away
float FADE = 1.;
// Effect duration in seconds
// NOTE: This is used to calculate FADE if LOOP is 0
const float DURATION = 4.0;

float ease(float t) {
    return pow(t, 0.125);
}

float easeInOut(float t) {
    return 1.0 - pow(2.0 * t - 1.0, 8.0);
}

float smin(float a, float b, float k) {
    float h = a - b;
    h = clamp((1.0 + h/k) / 2.0, 0.0, 1.0);
    return mix(a, b, h) - k*h*(1.0-h);
}

vec2 pixelate(vec2 uv) {
    float aspect = iResolution.x / iResolution.y;
    vec2 l = vec2(PIXEL_SIZE * aspect, PIXEL_SIZE);
    vec2 a = uv * iResolution.xy;
    vec2 p = round(a / l) * l;
    return p / iResolution.xy;
}

float smoothVoronoi(vec2 p, vec2 size, float smoothness) {
    p /= size;
    vec2 c = floor(p);

    vec2 centers[9];
    centers[0] = c + vec2( 0.0,  0.0); centers[0] += hash22(centers[0]);
    centers[1] = c + vec2( 0.0, +1.0); centers[1] += hash22(centers[1]);
    centers[2] = c + vec2( 0.0, -1.0); centers[2] += hash22(centers[2]);
    centers[3] = c + vec2(+1.0,  0.0); centers[3] += hash22(centers[3]);
    centers[4] = c + vec2(+1.0, +1.0); centers[4] += hash22(centers[4]);
    centers[5] = c + vec2(+1.0, -1.0); centers[5] += hash22(centers[5]);
    centers[6] = c + vec2(-1.0,  0.0); centers[6] += hash22(centers[6]);
    centers[7] = c + vec2(-1.0, +1.0); centers[7] += hash22(centers[7]);
    centers[8] = c + vec2(-1.0, -1.0); centers[8] += hash22(centers[8]);

    float d = length(p - centers[0]);
    vec2 cc = centers[0];

    for(int i = 1; i < 9; ++i) {
        d = smin(d, length(p - centers[i]), smoothness);
    }

    return d;
}

void droplets(in vec2 p, out float dropSDF, out float trailSDF, out float dropAlpha, out float trailAlpha, out vec3 dropNormal) {
    p.y += hash11(floor(p.x / DROP_SIZE.x)) * DROP_SIZE.y;

    vec2 cellID = floor(p / DROP_SIZE);
    vec2 cellPos = p - cellID * DROP_SIZE;

    float dropR = mix(0.25, 0.75, hash12(cellID)) * min(DROP_SIZE.x, DROP_SIZE.y) / 2.0 * pow(FADE, 0.5);
    float dropLife = mix(DROP_LIFE.x, DROP_LIFE.y, hash12(cellID)) * DURATION;

    #if LOOP
    float t = clamp(fract(iTime / dropLife), 0.0, 1.0);
    #else
    float t = clamp(iTime / dropLife, 0.0, 1.0);
    #endif
    float st = ease(t);

    vec2 dropHash = hash22(cellID);
    vec2 dropStart = vec2(dropHash.x, mix(0.66, 1.0, dropHash.y));
    dropStart = mix(vec2(dropR), DROP_SIZE - vec2(dropR), dropHash);

    vec2 dropEnd = vec2(dropHash.x, mix(0.0, 0.125, dropHash.y));
    dropEnd = mix(vec2(dropR), DROP_SIZE - vec2(dropR), dropEnd);

    vec2 dropCenter = mix(dropStart, dropEnd, t);

    vec2 trail = line_segment(cellPos, dropStart, dropCenter);

    float cst = pow(trail.y, 0.25);
    if (cellPos.y < dropCenter.y) 
       cst = 0.0;

    dropSDF = length(cellPos - dropCenter) - dropR * st;
    trailSDF = trail.x - dropR * cst;
    trailSDF = max(trailSDF, -dropSDF);
    dropAlpha = easeInOut(t);
    // trailAlpha = (1.0 - clamp(trailSDF / TRAIL_SMOOTH, 0.0,1.0)) * st;
    trailAlpha = clamp(-trailSDF / TRAIL_SMOOTH, 0.0, 1.0) * cst * st;

    dropNormal = normalize(vec3(
        normalize(cellPos - dropCenter),
        abs(dropSDF) / DROP_RIM
    ));

    // Fade normals
    dropNormal = mix(dropNormal, vec3(0,0,1),
        clamp(pow(length(cellPos - dropCenter) / (DROP_LIMITER * dropR), 8.0), 0.0, 1.0)
    );
}

vec4 blur(vec2 uv, vec2 size) {
    vec4 c = vec4(0);

    for (int x = 0; x < BLUR_SAMPLES; ++x) {
        for (int y = 0; y < BLUR_SAMPLES; ++y) {
            vec2 o = vec2(x, y) / vec2(BLUR_SAMPLES);
            o = 1.0 - 2.0 * o;
            o *= size;
            c += texture(iChannel0, uv + o);
        }
    }

    return c / float(BLUR_SAMPLES * BLUR_SAMPLES);
}

vec3 warp(vec3 p, vec3 size) {
    vec3 cell = floor(p / size);
    vec3 f = fract(p / size);

    vec3 corners[8];
    corners[0] = hash33(cell + vec3(0., 0., 0.));
    corners[1] = hash33(cell + vec3(1., 0., 0.));
    corners[2] = hash33(cell + vec3(0., 1., 0.));
    corners[3] = hash33(cell + vec3(1., 1., 0.));
    corners[4] = hash33(cell + vec3(0., 0., 1.));
    corners[5] = hash33(cell + vec3(1., 0., 1.));
    corners[6] = hash33(cell + vec3(0., 1., 1.));
    corners[7] = hash33(cell + vec3(1., 1., 1.));

    vec3 result = mix(
        mix(
            mix(corners[0], corners[1], f.x),
            mix(corners[2], corners[3], f.x),
            f.y),
        mix(
            mix(corners[4], corners[5], f.x),
            mix(corners[6], corners[7], f.x),
            f.y),
        // (1.0 -cos(f.z * 3.14159265)) / 2.0);
        f.z);

    result = -1.0 + 2.0 * result;
    result = normalize(result);

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    #if !LOOP
    FADE = 1. - clamp(iTime / DURATION, 0.0, 1.0);
    #endif

    vec2 uv = fragCoord / iResolution.xy;
    vec2 pres = iResolution.xy / PIXEL_SIZE;

    float mind = min(iResolution.x, iResolution.y);
    float maxd = max(iResolution.x, iResolution.y);
    vec2 auv = fragCoord / vec2(mind);
    auv.x -= (maxd - mind) / mind / 2.0;

    vec2 aspect = vec2(maxd / mind, 1.0);

    float dropSDF, trailSDF, dropAlpha, trailAlpha;
    vec3 dropNormal;

    #ifdef PIXELATE_DISTORT
    vec2 distort = vec2(
        1.0 - 2.0 * smoothVoronoi(pixelate(auv), DISTORT_SIZE, 0.25),
        1.0 - 2.0 * smoothVoronoi(pixelate(auv) + vec2(4765,3243), DISTORT_SIZE, 0.25)
    ) * DISTORT_STRENGTH;
    #else
    vec2 distort = vec2(
        1.0 - 2.0 * smoothVoronoi(auv, DISTORT_SIZE, 0.25),
        1.0 - 2.0 * smoothVoronoi(auv + vec2(4765,3243), DISTORT_SIZE, 0.25)
    ) * DISTORT_STRENGTH;
    #endif

    #ifdef PIXELATE_DROPLETS
    droplets(pixelate(auv) + distort, dropSDF, trailSDF, dropAlpha, trailAlpha, dropNormal);
    #else
    droplets(auv + distort, dropSDF, trailSDF, dropAlpha, trailAlpha, dropNormal);
    #endif

    float specular = dot(dropNormal, normalize(vec3(1.0)));
    specular = pow(specular, 8.0);

    float warpTime = iTime * FADE * FADE;
    #ifdef PIXELATE_WARP
    vec3 warpDistort =
        warp(vec3(pixelate(auv), warpTime), WARP_FREQ) * 3.0 +
        warp(vec3(pixelate(auv) * 2.0, warpTime + 6.0), WARP_FREQ) * 2.0 +
        warp(vec3(pixelate(auv) * 4.0, warpTime + 24.0), WARP_FREQ) * 1.0;
    #else
    vec3 warpDistort =
        warp(vec3(auv, warpTime), WARP_FREQ) * 3.0 +
        warp(vec3(auv * 2.0, warpTime + 6.0), WARP_FREQ) * 2.0 +
        warp(vec3(auv * 4.0, warpTime + 24.0), WARP_FREQ) * 1.0;
    #endif

    warpDistort = normalize(warpDistort) * WARP_STRENGTH * FADE;

    // Distort composite
    fragColor = texture(iChannel0, uv + mix(dropNormal.xy * DROP_DISTORT_STRENGTH, warpDistort.xy, clamp(dropSDF * 1024., 0., 1.)));
    float blurAlpha = trailAlpha;
    if (blurAlpha > 0.0) {
        fragColor = mix(fragColor, blur(uv, BLUR_SIZE * aspect) * vec4(1.0 - TRAIL_DARKEN), blurAlpha);
    }
    fragColor += vec4(specular);
}

