float smin(float a,float b) {
    float h = clamp(.5+.5*(a-b),.0,1.);
    return mix(a,b,h)-h*(1.-h);
}
#define a(x,y) smoothstep(x,y,(d-.5)/fwidth(d))
#define u(x) length(v+x)
#define l(x) for(;x<10;x++)
#define T iTime*.02
#define O(x) vec2(0,-cos(x))

void mainImage( out vec4 O, in vec2 c )
{
    vec2 r=iResolution.xy;
    vec2 v=7.*(2.*c-r)/r.y;

    float d=-v.y+4.+.01*pow(v.x,2.);
    int i;
    l(i) d=smin(d,u(5.*O(T+T*float(1+i*4))));

    d = a(2.,.5)-a(-.5,-2.);
    O = vec4(d);
}
