float burningship(vec2 z, vec2 c) {
    float l = 0.0;
    for (l = 0.0; l < 100.0; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, abs(2.0 * z.x * z.y)) + c;
        if(dot(z, z) > 65536.0) break;
    }
    return l - log2(log2(dot(z,z))) + 4.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = ((2.0 * fragCoord - iResolution.xy) / iResolution.x) * 2.0;
    vec2 ms = ((2.0 * iMouse.xy - iResolution.xy) / iResolution.x) * 2.0;

    if (iMouse.xy == vec2(0.0)) ms = vec2(-1.2, 0.3);

    vec2 mnduv = vec2(uv.x + 1.2, uv.y + 0.3) * 2.0;
    vec2 julms = vec2(ms.x + 1.2, ms.y + 0.3) * 2.0;
    vec2 juluv = vec2(uv.x - 1., uv.y);

    vec3 col = vec3(0);

    if (fragCoord.x > iResolution.x / 2.0) {
        float j = burningship(juluv * 2.0, -julms);
        col = vec3((j > 99.0) ? 0.0 : sin(j / 20.0)) * vec3(1.8, .7, .2);
    } else {
        float m = burningship(vec2(0), -mnduv);
        col = vec3((m > 99.0) ? 0.0 : sin(m / 20.0)) * vec3(1.8, .7, .2);
    }

    float p = length(uv - ms) - 0.025;

    if (p < 0.0) fragColor = vec4(vec3(1.0, 0.0, 0.0), 1.0);
    else fragColor = vec4(col, 1.0);
}
