//Screen
vec3 screen(vec3 base, vec3 bl_layer){
    return base + bl_layer - base * bl_layer;
}

//HardLight 
vec3 hardLight(vec3 base, vec3 bl_layer){
    vec3 temp_result;
        if (bl_layer.r < 0.5)
            temp_result.r = 2.0 * base.r * bl_layer.r;
        else
            temp_result.r = 1.0 - 2.0 * (1.0 - base.r) * (1.0 - bl_layer.r);
        if (bl_layer.g < 0.5)
            temp_result.g = 2.0 * base.g * bl_layer.g;
        else
            temp_result.g = 1.0 - 2.0 * (1.0 - base.g) * (1.0 - bl_layer.g);
        if (bl_layer.b < 0.5)
            temp_result.b = 2.0 * base.b * bl_layer.b;
        else
            temp_result.b = 1.0 - 2.0 * (1.0 - base.b) * (1.0 - bl_layer.b);
    return temp_result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex1 = texture(iChannel1, uv);

    // Output to screen
    fragColor = vec4(screen(tex0.rgb, tex1.rgb),1.0);
}
