#define PI 3.14159265358979323846

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 scale2D(vec2 _st, vec2 _scale){
    _st -= 0.5;
    _st = mat2(_scale.x,0.0,
               0.0,_scale.y) * _st;
    _st += 0.5;
    return _st;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tex0 = texture(iChannel0, uv);
    vec4 tex0t = texture(iChannel0, scale2D(uv, vec2(0.5)+0.05*sin((iTime/4.))))*0.95;
    tex0t.b = texture(iChannel0, scale2D(uv, vec2(0.5)+0.05*sin((iTime/4.)))+vec2(0.01*cos(iTime), 0.01*sin(iTime))).b*0.95;

    vec4 tex0b = texture(iChannel0, scale2D(uv, vec2(0.5)+0.05*sin((iTime/4.))))*0.95;
    tex0b.r = texture(iChannel0, scale2D(uv, vec2(0.5)+0.05*cos((iTime/4.)))-vec2(0.01*sin(iTime), 0.01*cos(iTime))).r*0.95;


    // Output to screen
    fragColor = mix(tex0, tex0t, smoothstep(0.8,0.85, rotate2D(uv, -PI*0.2).y));
    fragColor = mix(fragColor, tex0b, 1.-smoothstep(0.19, 0.24, rotate2D(uv, -PI*0.2).y));
}
