float superSamples = 100.0;

// Seed
float cs = 1.0;

vec2 hash22(float p) {
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

float sampler(vec2 p, float itr) {
    vec2 z = vec2(0.0);
    float l = 0.0;
    for (l = 0.0; l < itr; l += 1.0) {
        z = vec2(z.x * z.x - z.y * z.y, abs(2.0 * z.x * z.y)) + p;
        if(dot(z, z) > 65536.0) break;
    }
    return l - log2(log2(dot(z, z))) + 4.0;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float l = 0.0;
    for (float ss = 0.0; ss < superSamples; ss += 1.0) {
        vec2 uv = ((2.0 * (fragCoord + hash22(cs) / 2.0) - iResolution.xy) / -iResolution.y) / 15.0;
        uv = vec2(uv.x - 1.76, uv.y - 0.03);
        l += sampler(uv, 1000.0);
        cs += 1.0;
    }
    l /= superSamples;
    l = (l > 99.0) ? 0.0 : sin(l / 20.0);
    fragColor = vec4(vec3(1.9, .7, .2) * pow(l, 2.0), 1.0);
}
