void mainImage( out vec4 c, in vec2 o )
{
    vec2 uv = (o.xy)/iResolution.xy;float t = iTime;
    vec2 coord = o - (iResolution.xy / vec2(2.0));
    float x = (coord.x);
    float y = float(coord.y);
    float r2 = float(x*x+ y*y)/32.0;

	c = vec4(
     texture(iChannel0, uv).x,
     texture(iChannel0, uv).y,
     texture(iChannel0, uv).z,
    1.0);

    if(sqrt(r2+8.0)<=16.0){
      c/=r2/256.0;
    };

    if (sqrt(r2+8.0)>=16.0&&sqrt(r2+8.0)<=16.2){
      c.x*=1.1; c.y/=1.1;
      c*=vec4(sqrt(floor(abs(x)+abs(y)))/floor(abs(abs(x)-abs(y))))*9.0;
     }

}
